import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

const fault = [];
const array = [];
const temp=[];
const temp2=[];
const test2 = [];
const up = {};
function getSpecificFault(){
  axios
  .get("http://localhost:8080/api/v1/category/specific/"+lastURLSegment)
  .then((response) => {
      fault.push(response.data)
      array.push(fault[0])
      temp.push(Object.values(array[0]))
      temp2.push(Object.keys(array[0]))
      console.log('Temp');
      console.log(temp[0][0]);
      console.log('what');
      console.log(temp[0][0].radio.name);
  })
}
getSpecificFault();


export default function EditFault(){
    const [displayfaultname,setDisplayFaultName] = useState(temp[0][0].name);
    const [displayhaveradio,setDisplayHaveRadio] = useState(temp[0][0].haveRadio);
    const [displayhaveinput,setDisplayHaveInput] = useState(temp[0][0].haveInput);
    const [displayhavecheckbox,setDisplayHaveCheckbox] = useState(temp[0][0].haveCheck);
    
    //for all answer state
    const [faultname,setFaultName] = useState("");
    const [radioquestion,setRadioQuestion] = useState("");
    const [radioanswer,setRadioAnswer] = useState("");
    const [inputquestion,setInputQuestion] = useState("");
    const [checkboxquestion1,setCheckboxQuestion1] = useState('');
    const [checkboxanswer1,setCheckboxAnwer1] = useState("");
    const [checkboxquestion2,setCheckboxQuestion2] = useState("");
    const [checkboxanswer2,setCheckboxAnwer2] = useState("");

    function displayRadio(){
        if(displayhaveradio == "true"){
            let radio = Object.values(temp[0][0].radio);
            return(
                
                <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h3><b>Radio</b></h3>
                        </CardHeader>
                        <CardBody>
                        <h4>Radio Question :</h4>
                        <input type="text" className="form-control" value={radio.map(radi =>{return radi.name})} isDisabled="true" /> 
                        <br></br>
                        <h4>Radio Answer :</h4>
                        <input type="text" className="form-control" value={radio.map(radi =>{return radi.answer})} isDisabled="true" /> 
                        </CardBody>
                    </Card>
                </GridItem>
            )
        }else{
    
        }
    }

    function displayCheckbox(){
        if(displayhavecheckbox == "true"){
            let check = Object.values(temp[0][0].checkbox);
            if(check.length > 1){
                return(
                    <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h3><b>Checkbox</b></h3>
                        </CardHeader>
                        <CardBody>
                            <h4>Checkbox Question :</h4>
                            <input className="form-control" type="text" value={check[0].name} placeholder="Enter Checkbox Question.."/>
                            <br></br>
                            <h4>Checkbox Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                            <input className="form-control" type="text" value={check[0].answer} placeholder="Enter Checkbox Answer.."/>
                            <br></br>
                            <h4>Checkbox Question 2 :</h4>
                            <input  className="form-control" type="text" value={check[1].name} placeholder="Enter Checkbox Question 2.."/>
                            <br></br>
                            <h4>Checkbox Answer 2 (Text will be split into using (,) e.g Apple,Orange) :</h4>
                            <input className="form-control" type="text" value={check[1].answer} placeholder="Enter Checkbox Answer 2.."/>
                        </CardBody> 
                    </Card>
                    </GridItem>
                )
            }else{
                return(
                    <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h3><b>Checkbox</b></h3>
                        </CardHeader>
                        
                        <CardBody>
                            <h4>Checkbox Question :</h4>
                            <input className="form-control"  value={check[0].name} type="text" placeholder="Enter Checkbox Question.."/>
                            <br></br>
                            <h4>Checkbox Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                            <input className="form-control" value={check[0].answer} type="text" placeholder="Enter Checkbox Answer.."/>
                            
                        </CardBody> 
                    </Card>
                    </GridItem>
                )
            }
        }else{
        }
    }

    function displayInput(){
        if(displayhaveinput == "true"){
            return(
                <GridItem xs={12} sm={12} md={12}>
                <Card>
                        <CardHeader>
                            <h3><b>Input</b></h3>
                        </CardHeader>
                        <CardBody>
                        <h4>Input Question:</h4>
                        <input className="form-control" onChange={e=>setFaultName(e.target.value)} type="text" on value={temp[0][0].input}placeholder="Enter Input Question.."/>
                        </CardBody>
                    </Card>
                </GridItem>
            )
        }else{
    
        }
    }
   
    return(
        <div>
        <h3><b>Edit Fault</b></h3>
        <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
                <Card>
                    <CardBody>
                    <h4><b>Fault Name (Category) :</b></h4>
                    <input className="form-control" onChange={e=>setFaultName(e.target.value)} type="text" placeholder="Edit Fault Name, example..Electric Griller" defaultValue={displayfaultname} onChange={e=>setFaultName(e.target.value)}/> 
                    <br></br>
                <h4><b>Have Radio :</b></h4>
                <Select
                    placeholder={haveradio}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                <br></br>
                <h4><b>Have Checkbox :</b></h4>
                <Select
                    placeholder={havecheckbox}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                <br></br>
                <h4><b>Have Input :</b></h4>
                <Select
                    placeholder={haveinput}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                </CardBody>
                </Card>
            </GridItem> 
            {
                displayRadio()
            }
            {
                displayCheckbox()
            }
            {
                displayInput()
            }
             {/* <Button onClick={submit} fullWidth color="success">Add</Button>  */}
        </GridContainer>
    </div>
    )
}
