import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

export default function Addstore(){
    function addStore(){
        axios
        .post("http://localhost:8080/api/v1/store/",{"qrstring": edit.toString() })
        window.location.href = "/admin/functions"
    }
    return(
        <div>
            <h3><b>Add Store</b></h3>
            <GridContainer>
            <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Name :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Store Name, example..Hillion Mall"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Code :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Store Code, last 4 digit of store no"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Address :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Store Address"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={event =>  window.location.href='/admin/dashboard'} fullWidth color="success">Add</Button> 
                </GridItem>
            </GridContainer>
        </div>
    )
}
