import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';



const useStyles = makeStyles(styles);

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);
//console.log(lastURLSegment);

const displaynewCases = [];
const array =[];
const testarr =[];
function getNewCases(){
    axios
    .get("http://localhost:8080/api/v1/fault/"+lastURLSegment)
    .then((response) => {
        displaynewCases.push(response.data)
        console.log("below");
        console.log(displaynewCases);
    //     try{
    //   displaynewCases.push(response.data)
    //   //console.log(displaynewCases);
    //   array.push(displaynewCases[0])
    //   testarr.push(Object.values(array[0]))
    //   console.log("xia mian");
    //   console.log(testarr);
    //     }
    //     catch(error){
    //         console.log(error)
    //     }
  })
  }
  getNewCases();

  //var image = displaynewCases[0].imageurl;
  function retrieveImg(){
    axios
    .get("http://localhost:8080/api/v1/image?location=f9e501f5-1f41-4572-a5db-906fe44f0967/images/9ad52079-fb94-4bc5-8d1f-cc408b231933.jpg")
    .then((response) => {
        console.log("below");
        console.log(response.data);
})
}
retrieveImg();


export default function Solve() {
    //const [search, setSearch] = useState('')
    const classes = useStyles();
    const statusOptions  = [{value:"unresolved",label:"Unresolved"},{value:"pending",label:"Pending"},{value:"resolved",label:"Resolved"}]
    
    return (
        <div>
            <h3><b>Solve Case</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Reported on:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{displaynewCases[0].dateTime}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Location:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{displaynewCases[0].storeName}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Staff Reported:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{displaynewCases[0].staffName}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Fault Type:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{displaynewCases[0].problem.category}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <h3><b>Incident Details</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4>Fault Image:</h4>
                        </CardHeader>
                        <CardBody>
                        {displaynewCases[0].imageurl}
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={8}>
                    <Card>
                        <CardHeader>
                            <h4>Description:</h4>
                        </CardHeader>
                        <CardBody>
                        {displaynewCases[0].description}
                        </CardBody>
                        <CardHeader>
                            <h4>Issue Status:</h4>
                        </CardHeader>
                        <CardBody>
                            <Select
                            defaultValue={{value:"unresolved",label:"Unresolved"}}
                            className="basic-single"
                            classNamePrefix="select"
                            name="color"
                            options={statusOptions}
                            onChange={opt=>console.log(opt.value)}
                            />
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={event =>  window.location.href='/admin/dashboard'} fullWidth color="success">Save</Button> 
                </GridItem>
            </GridContainer>
        </div>
    );
  }
