import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

export default function AddFault(){
    const haveRadio = [{ value: "true", label: "True" }, { value: "false", label: "False" }]
    const haveCheckbox = [{ value: "true", label: "True" }, { value: "false", label: "False" }]
    const haveInput = [{ value: "true", label: "True" }, { value: "false", label: "False" }]
    const [radio,setRadio] = useState("false");
    const [checkbox,setCheckbox] = useState("false");
    const [input,setInput] = useState("false");
    function displayRadio(){
        if(radio == "true"){
            return(
                <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h3><b>Radio</b></h3>
                        </CardHeader>
                        <CardBody>
                        <h4>Radio Question :</h4>
                        <input className="form-control" type="text" placeholder="Enter Radio Question.."/>
                        <br></br>
                        <h4>Radio Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                        <input className="form-control" type="text" placeholder="Enter Radio Answer.."/>
                        </CardBody>
                    </Card>
                </GridItem>
            )
        }else{

        }
    }
    function displayCheckbox(){
        if(checkbox == "true"){
            return(
                <GridItem xs={12} sm={12} md={12}>
                <Card>
                    <CardHeader>
                        <h3><b>Checkbox</b></h3>
                    </CardHeader>
                    <CardBody>
                        <h4>Checkbox Question :</h4>
                        <input className="form-control" type="text" placeholder="Enter Checkbox Question.."/>
                        <br></br>
                        <h4>Checkbox Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                        <input className="form-control" type="text" placeholder="Enter Checkbox Answer.."/>
                        <br></br>
                        <h4>Checkbox Question 2 :</h4>
                        <input className="form-control" type="text" placeholder="Enter Checkbox Question 2.."/>
                        <br></br>
                        <h4>Checkbox Answer 2 (Text will be split into using (,) e.g Apple,Orange) :</h4>
                        <input className="form-control" type="text" placeholder="Enter Checkbox Answer 2.."/>
                    </CardBody> 
                </Card>
                </GridItem>
            )
        }else{
        }
    }
    function displayInput(){
        if(input == "true"){
            return(
                <GridItem xs={12} sm={12} md={12}>
                <Card>
                        <CardHeader>
                            <h3><b>Input</b></h3>
                        </CardHeader>
                        <CardBody>
                        <h4>Input Question:</h4>
                        <input className="form-control" type="text" placeholder="Enter Input Question.."/>
                        </CardBody>
                    </Card>
                </GridItem>
            )
        }else{

        }
    }
    return(
        <div>
            <h3><b>Add Fault</b></h3>
            <GridContainer>
            <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h4><b>Fault Name (Category) :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Fault Name.."/>
                        <br></br>
                    <h4><b>Have Radio :</b></h4>
                    <Select
                        defaultValue={{ value: "false", label: "False" }}
                        className="basic-single"
                        classNamePrefix="select"
                        name="color"
                        options={haveRadio}
                        onChange={e=>setRadio(e.value)}
                        />
                    <br></br>
                    <h4><b>Have CheckBox :</b></h4>
                    <Select
                        defaultValue={{ value: "false", label: "False" }}
                        className="basic-single"
                        classNamePrefix="select"
                        name="color"
                        options={haveCheckbox}
                        onChange={e=>setCheckbox(e.value)}
                        />
                    <br></br>
                    <h4><b>Have Input :</b></h4>
                    <Select
                        defaultValue={{ value: "false", label: "False" }}
                        className="basic-single"
                        classNamePrefix="select"
                        name="color"
                        options={haveInput}
                        onChange={e=>setInput(e.value)}
                        />
                    </CardBody>
                    </Card>
                </GridItem> 
                {
                    displayRadio()
                }
                {
                    displayCheckbox()
                }
                {
                    displayInput()
                }
            </GridContainer>
        </div>
    )
}
