import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import axios from 'axios';

const temp =[];

const columns = [
  { id: 'datetime', label: 'Cases Report On', minWidth: 170 },
  { id: 'storelocation', label: 'Store Location', minWidth: 100 },
  { id: 'category', label: 'Category', minWidth: 100 },
  { id: 'description', label: 'Description', minWidth: 100 },
  { id: 'staffname', label: 'Staff Name', minWidth: 100 },
  { id: 'comment', label: 'Comments', minWidth: 100 },
];



const rows = [
    temp.map((temp) => {
        return [temp.dateTime,temp.storeName,temp.problem.category,temp.description,temp.staffName,temp.comments]
    })
];

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

export default function StickyTable() {
  const [testtemp,setTestTemp] = React.useState();
  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const resolvedCases = [];
    const array=[];
    function getResolvedCases(){
    axios
    .get("http://localhost:8080/api/v1/fault?status=Resolved")
    .then((response) => {
        try{
        resolvedCases.push(response.data)
        temp.push(Object.values(resolvedCases[0]))
        console.log(temp[0]);
        setTestTemp(temp[0])
        }
        catch(error){
        console.log(error);
        }
    })
    }
    getResolvedCases();

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
