//   }
//   //console.log("TEMP" + temparray);
// }
// //looper();
const newArray=[];

export default function newcaseModal(props){
  const temparray=[];
  const temparray2=[];
  for(var i=0; i<array.length;i++){
    var object = {value:[array[i].value[0], array[i].value[1], array[i].value[2] ,<Button onClick={event =>  window.location.href='/newcases/solve'} fullWidth color="info">Solve</Button>]}
    temparray.push(object)
  }
  console.log(temparray);
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      backdrop="static"
      keyboard={false}
    >
      <ModalHeader closeButton >
        <ModalTitle id="contained-modal-title-vcenter">
          Unresolved Issues
        </ModalTitle>
      </ModalHeader>
      <ModalBody>
      <Fragment>
        <p><b>Store Location</b></p>
        <Select
          className="basic-single"
          classNamePrefix="select"
          name="color"
          options={storeOptions}
        />
      </Fragment>
      <Table
              tableHeaderColor="primary"
              tableHead={["Reported on", "Fault type", "Store Location", ""]}
              tableData={
                testarray.map((testarray) => {
                  return [testarray.DateTime,testarray.Problem.Category,testarray.StoreName,<Button onClick={event =>  window.location.href='/newcases/solve'} fullWidth color="info">Solve</Button>]
                })
              }
            /> 
      </ModalBody>
      <ModalFooter>
        <Button color="danger" onClick={props.onHide}>Close</Button>
      </ModalFooter>
    </Modal>
  );
}

