import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

export default function AddFault(){
    const haveRadio = [{ value: "true", label: "True" }, { value: "false", label: "False" }]
    const [radio,setRadio] = useState("false");
    function displayRadio(){
        if(radio == "true"){
            return(
                <GridContainer>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Radio Question :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Radio Question.."/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                <Card>
                        <CardHeader>
                            <h4><b>Radio Answer (Text will be split into using (,) e.g Apple,Orange) :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Radio Answer.."/>
                        </CardBody>
                    </Card>
                </GridItem>
                </GridContainer>
            )
        }else{

        }
    }
    return(
        <div>
            <h3><b>Add Fault</b></h3>
            <GridContainer>
            <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Fault Name (Category) :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Enter Fault Name.."/>
                        <br></br>
                    <h4><b>Have Radio :</b></h4>
                    <Select
                        defaultValue={{ value: "false", label: "False" }}
                        className="basic-single"
                        classNamePrefix="select"
                        name="color"
                        options={haveRadio}
                        onChange={e=>setRadio(e.value)}
                        />
                    <h4><b>Have CheckBox :</b></h4>
                    <Select
                        defaultValue={{ value: "false", label: "False" }}
                        className="basic-single"
                        classNamePrefix="select"
                        name="color"
                        options={haveRadio}
                        onChange={e=>setRadio(e.value)}
                        />
                    </CardBody>
                    </Card>
                </GridItem>
               
                       
                {
                    displayRadio()
                }
            </GridContainer>
        </div>
    )
}
