import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import axios from 'axios';

function getCustomerFeedbacks(){
  axios
  .get("http://localhost:8080/api/v1/feedback")
  .then((response) => {
    try{
    newCases.push(response.data)
    array.push(Object.values(newCases[0]))
    //testarr.push(Object.values(array[0]))
    console.log(array[0]);
    }
    catch(error){
      console.log(error);
    }
})
}
getCustomerFeedbacks();

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const useStyles = makeStyles(styles);

export default function TableList() {
  const classes = useStyles();
  return (
    <GridContainer>
      <GridItem xs={12} sm={12} md={12}>
        <Card>
          <CardHeader color="primary">
            <h4 className={classes.cardTitleWhite}>Customer Feedbacks</h4>
            <p className={classes.cardCategoryWhite}>
              Customer Feedbacks for every outlets
            </p>
          </CardHeader>
          <CardBody>
            <Table
              tableHeaderColor="primary"
              tableHead={["ID", "Store Location", "Feedback Type", "Standard", "Issue", "Ratings", "Served By"]}
              tableData={[
                ["1","Hillion Mall", "Service-Related", "Complaint", "Rude Staff, scolded my daughter" ,"1 star" ,"Mary Sac (18456)"],
                ["2","Causeway Point", "Service-Related", "Compliment", "Polite and Patient" ,"5 star" ,"John Doe (18556)"],
                ["3","Vivo City", "Product-Related", "Complaint", "Expired Ba Kua" ,"0 star" ,"Vivian (18436)"],
                ["4","West Mall", "Service-Related", "Compliment", "Polite staff" ,"4 star" ," Jason Toh (18596)"],
                ["5","JCube", "Product-Related", "Compliment", "Fresh and Fragrant Ba Kua" ,"5 star" ,"Darren Kong (18112)"],
                ["6","Bedok Mall", "Product-Related", "Compliment", "Fresh Pork Floss" ,"3 star" ,"Savet Oh (18213)"]
              ]}
            />
          </CardBody>
        </Card>
      </GridItem>
    </GridContainer>
  );
}
