import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

function getSpecificQR(){
    axios
    .get("http://localhost:8080/api/v1/store2/"+lastURLSegment)
    .then((response) => {
        //console.log("hello");
        //console.log(displaynewCases[0].imageurl);
    //     try{
       displaynewCases.push(response.data)
      
    //   array.push(displaynewCases[0])
    //   testarr.push(Object.values(array[0]))
    //   console.log("xia mian");
    //   console.log(testarr);
    //     }
    //     catch(error){
    //         console.log(error)
    //     }
  })
  }

  getSpecificQR();

export default function Editqrcode(){
    return(
        <div>
            <h3><b>Edit QR Code</b></h3>
            <GridContainer>
            <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Location :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text"/>
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <GridContainer>
            <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Name :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Code :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Qr String :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text"/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={event =>  window.location.href='/admin/dashboard'} fullWidth color="success">Save</Button> 
                </GridItem>
            </GridContainer>
        </div>
    )
}
