import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";
import { Container, List } from "semantic-ui-react";
import Dropdown from "components/DropdownAna/dropdown.js";
import axios from 'axios';
import { Fragment } from 'react';

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const newCases = [];
const array = [];

const download = function (data) {
  const blob = new Blob([data], { type: 'text/csv' });
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.setAttribute('hidden', '');
  a.setAttribute('href', url);
  a.setAttribute('download', 'download.csv');
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
};


function getNewCases() {
  axios
    .get("http://localhost:8080/api/v1/fault")
    .then((response) => {
      newCases.push(response.data)
      array.push(Object.values(newCases[0]))
      console.log(array);

      const data = array[0].map(row => ({
        Comments: row.comments,
        DateTime: row.dateTime,
        Description: row.description,
        Category: row.problem.category,
        StaffName: row.staffName,
        StoreName: row.storeName
      }));
      console.log('sadas');
      //console.log(data);

      const csvRows = [];

      //get the headers
      const headers = Object.keys(data[0]);

      csvRows.push(headers.join(','));

      //loop over rows
      for (const row of data) {
        const values = headers.map(header => {

          return row[header];
        });
        csvRows.push(values.join(','));
      }
      //form escaped comma seperated values
      const newdata = csvRows.map(e => e).join('\n');
      console.log(newdata);
      download(newdata);
    })

}





export default function Analytics() {
  return (
    <Fragment>
      <p>Generate Excel Sheet</p>
      <Button onClick={() => getNewCases()} fullWidth color="info">Generate Excel Sheet</Button>
    </Fragment>
  );
}
