export default function EditFault(){
    const [faultname,setFaultName] = useState(temp[0].map(item=>{
        return item.name
    }));
    const [haveradio,setHaveRadio] = useState(temp[0].map(item=>{
        return item.haveRadio
    }));
    const [haveinput,setHaveInput] = useState(temp[0].map(item=>{
        return item.haveInput
    }));
    const [havecheckbox,setHaveCheckbox] = useState(temp[0].map(item=>{
        return item.haveCheck
    }));
    // const [haveradiofordisplay,setHaveRadioForDisplay] = useState(temp[0].radio.map(item=>{
    //     return item.radioquestion
    // }));
    //console.log(haveradiofordisplay);

    function displayRadio(){
        if(haveradio == "true"){
            return(
                <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h3><b>Radio</b></h3>
                        </CardHeader>
                        <CardBody>
                        <h4>Radio Question :</h4>
                        {/* <input type="text" className="form-control" value={haveradiofordisplay} isDisabled="true" /> */}
