import React, {useState, useEffect} from 'react';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import * as ReactBootStrap from 'react-bootstrap';
import axios from 'axios';

export default function Analytics2() {
    const [analytics,setAnalytics] = useState([]);
    const getAnalyticsData = async ()=> {
        try{
            await axios
            .get("http://localhost:8080/api/v1/fault?status=Resolved")
            .then((response) => {
                console.log(response.data);
                setAnalytics(Object.values(response.data));
            })
        }
        catch(e){
            console.log(e);
        }
    }

    const columns = [
        {dataField: "dateTime", text:"Case reported on"},
        {dataField: "storeName", text:"Store Location"},
        {dataField: "problem.category", text:"Category"},
        {dataField: "description", text:"Description"},
        {dataField: "staffName", text:"Staff Name"},
        {dataField: "comments", text:"Comments"},
    ]

    useEffect(() => {
        getAnalyticsData();
    },[])
    return (
        <BootstrapTable
        color="primary"
        keyField="uuid"
        data={analytics}
        columns={columns}
        pagination={paginationFactory()}
        />
    )
}
