import React from "react";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";
import { Container, List } from "semantic-ui-react";
import {Fragment} from 'react';
import axios from 'axios';



const newCases = [];
const array = [];

const download = function (data) {
  const blob = new Blob([data], { type: 'text/csv' });
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.setAttribute('hidden', '');
  a.setAttribute('href', url);
  a.setAttribute('download', 'download.csv');
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
};


function getNewCases() {
  axios
    .get("http://localhost:8080/api/v1/fault")
    .then((response) => {
      newCases.push(response.data)
      array.push(Object.values(newCases[0]))
      console.log(array);

      const data = array[0].map(row => ({
        Comments: row.comments,
        ReportedOn: row.dateTime,
        Description: row.description,
        Category: row.problem.category,
        StaffName: row.staffName,
        StoreName: row.storeName
      }));
      console.log('sadas');
      //console.log(data);

      const csvRows = [];

      //get the headers
      const headers = Object.keys(data[0]);

      csvRows.push(headers.join(','));

      //loop over rows
      for (const row of data) {
        const values = headers.map(header => {

          return row[header];
        });
        csvRows.push(values.join(','));
      }
      //form escaped comma seperated values
      const newdata = csvRows.map(e => e).join('\n');
      console.log(newdata);
      download(newdata);
    })

}





export default function Analytics() {
  return (
    <div>
    <GridContainer>
    <GridItem xs={12} sm={12} md={12}>
      <Card>
        <CardHeader color="primary">
          <h4>Resolved Cases</h4>
          <p>
            Customer Feedbacks for every outlets
          </p>
        </CardHeader>
        <CardBody>
        <input className="form-control" type="text" placeholder="Search" onChange={ e => setSearch(e.target.value)}/>
          <Table
            tableHeaderColor="primary"
            tableHead={["Submitted On", "Store Location", "Feedback Type", "Standard", "Issue", "Ratings", "Served By", "Customer email", "Customer Contact", "Customer Name"]}
            tableData={
              filterArray.map((array) => {
                return [array.datetime,array.location,array.feedbackType,array.standard,array.issue,array.rating,array.staffname,array.custemail,array.custcontactno,array.custname]
            })
            }
          />
        </CardBody>
      </Card>
    </GridItem>
  </GridContainer>
    <GridContainer>
      <GridItem xs={12} sm={12} md={12}>
      {/* <p>Generate Excel Sheet</p> */}
      <Button onClick={() => getNewCases()} fullWidth color="info">Generate Excel Sheet</Button>
      </GridItem>
    </GridContainer>
    </div>
  );
}
