import React, {useState, useEffect} from 'react';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import * as ReactBootStrap from 'react-bootstrap';
import axios from 'axios';
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import Select from 'react-select';


export default function Analytics2() {
    const [analytics,setAnalytics] = useState([]);
    const getAnalyticsData = async ()=> {
        try{
            await axios
            .get("http://localhost:8080/api/v1/fault?status=Resolved")
            .then((response) => {
                console.log(response.data);
                setAnalytics(Object.values(response.data));
            })
        }
        catch(e){
            console.log(e);
        }
    }

    const columns = [
        {dataField: "dateTime", text:"Case reported on"},
        {dataField: "storeName", text:"Store Location"},
        {dataField: "problem.category", text:"Category"},
        {dataField: "description", text:"Description"},
        {dataField: "staffName", text:"Staff Name"},
        {dataField: "comments", text:"Comments"},
    ]

    useEffect(() => {
        getAnalyticsData();
    },[])
    return (
        <Card>
        <CardHeader color="primary">
        <h4>Cases</h4>
          <p>
            Cases can be generated as excel sheet and downloaded
          </p>
        </CardHeader>
        <CardBody>
        <b><h4>Filter :</h4></b>
        <br></br>
        <h6>By Category :</h6>
        <Select
          className="basic-single"
          classNamePrefix="select"
          name="color"
        />
        <br></br>
        <h6>By Store Location :</h6>
        <Select
          className="basic-single"
          classNamePrefix="select"
          name="color"
        />
        <br></br>
        <h6>By Date :</h6>
        <Select
          className="basic-single"
          classNamePrefix="select"
          name="color"
        />
        <br></br>
        <BootstrapTable
        keyField="uuid"
        data={analytics}
        columns={columns}
        pagination={paginationFactory()}
        />
        </CardBody>
        </Card>
    )
}
