import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

const fault = [];
const array = [];
const temp=[];
const temp2=[];
function getSpecificFault(){
  axios
  .get("http://localhost:8080/api/v1/category/specific/"+lastURLSegment)
  .then((response) => {
      fault.push(response.data)
      array.push(fault[0])
      temp.push(Object.values(array[0]))
      temp2.push(Object.keys(array[0]))
      console.log("NEW");
      console.log(temp);
  })
}
getSpecificFault();

function displayRadio(){
    if(radio == "true"){
        return(
            <GridItem xs={12} sm={12} md={12}>
                <Card>
                    <CardHeader>
                        <h3><b>Radio</b></h3>
                    </CardHeader>
                    <CardBody>
                    <h4>Radio Question :</h4>
                    <input type="text" className="form-control" value={haveradiofordisplay} isDisabled="true" />
                    <br></br>
                    <h4>Radio Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                    <input onChange={e=>setRadioAnswer(e.target.value)} className="form-control" type="text" placeholder="Enter Radio Answer.."/>
                    </CardBody>
                </Card>
            </GridItem>
        )
    }else{

    }
}
function displayCheckbox(){
    if(checkbox == "true"){
        return(
            <GridItem xs={12} sm={12} md={12}>
            <Card>
                <CardHeader>
                    <h3><b>Checkbox</b></h3>
                </CardHeader>
                <CardBody>
                    <h4>Checkbox Question :</h4>
                    <input onChange={e=>setCheckboxQuestion1(e.target.value)} className="form-control" type="text" placeholder="Enter Checkbox Question.."/>
                    <br></br>
                    <h4>Checkbox Answer (Text will be split into using (,) e.g Apple,Orange) :</h4>
                    <input onChange={e=>setCheckboxAnwer1(e.target.value)}className="form-control" type="text" placeholder="Enter Checkbox Answer.."/>
                    <br></br>
                    <h4>Checkbox Question 2 :</h4>
                    <input onChange={e=>setCheckboxQuestion2(e.target.value)} className="form-control" type="text" placeholder="Enter Checkbox Question 2.."/>
                    <br></br>
                    <h4>Checkbox Answer 2 (Text will be split into using (,) e.g Apple,Orange) :</h4>
                    <input onChange={e=>setCheckboxAnwer2(e.target.value)} className="form-control" type="text" placeholder="Enter Checkbox Answer 2.."/>
                </CardBody> 
            </Card>
            </GridItem>
        )
    }else{
    }
}
function displayInput(){
    if(input == "true"){
        return(
            <GridItem xs={12} sm={12} md={12}>
            <Card>
                    <CardHeader>
                        <h3><b>Input</b></h3>
                    </CardHeader>
                    <CardBody>
                    <h4>Input Question:</h4>
                    <input onChange={e=>setInputQuestion(e.target.value)} className="form-control" type="text" placeholder="Enter Input Question.."/>
                    </CardBody>
                </Card>
            </GridItem>
        )
    }else{

    }
}

export default function EditFault(){
    const [faultname,setFaultName] = useState(temp[0].map(item=>{
        return item.name
    }));
    const [haveradio,setHaveRadio] = useState(temp[0].map(item=>{
        return item.haveRadio
    }));
    const [haveinput,setHaveInput] = useState(temp[0].map(item=>{
        return item.haveInput
    }));
    const [havecheckbox,setHaveCheckbox] = useState(temp[0].map(item=>{
        return item.haveCheck
    }));
    const [haveradiofordisplay,setHaveRadioForDisplay] = useState(temp[0].radio.map(item=>{
        return item.name
    }));
   
    return(
        <div>
        <h3><b>Edit Fault</b></h3>
        <GridContainer>
        <GridItem xs={12} sm={12} md={12}>
                <Card>
                    <CardBody>
                    <h4><b>Fault Name (Category) :</b></h4>
                    <input className="form-control" type="text" placeholder="Edit Fault Name, example..Electric Griller" defaultValue={faultname} onChange={e=>setFaultName(e.target.value)}/> 
                    <br></br>
                <h4><b>Have Radio :</b></h4>
                <Select
                    placeholder={haveradio}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                <br></br>
                <h4><b>Have Checkbox :</b></h4>
                <Select
                    placeholder={havecheckbox}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                <br></br>
                <h4><b>Have Input :</b></h4>
                <Select
                    placeholder={haveinput}
                    className="basic-single"
                    classNamePrefix="select"
                    name="color"
                    isDisabled="true"
                    />
                </CardBody>
                </Card>
            </GridItem> 
            {
                displayRadio()
            }
            {/* {
                displayCheckbox()
            }
            {
                displayInput()
            } */}
             {/* <Button onClick={submit} fullWidth color="success">Add</Button>  */}
        </GridContainer>
    </div>
    )
}
