import React from 'react';
import { Fragment } from 'react';
import Select from 'react-select';
import Button from "components/CustomButtons/Button.js";
import Modal from 'react-bootstrap/Modal';
import ModalHeader from 'react-bootstrap/ModalHeader';
import ModalTitle from 'react-bootstrap/ModalTitle';
import ModalBody from 'react-bootstrap/ModalBody';
import ModalFooter from 'react-bootstrap/ModalFooter';
import Table from "components/Table/Table.js";
import axios from 'axios';



const newCases = [];
const array =[];
const temparray=[];
function getNewCases(){
  axios
  .get("http://localhost:8080/api/v1/newcases")
  .then((response) => {
    newCases.push(response.data)  
    newCases[0].map(item=>{
      var object = {value:[item.DateTime,item.Problem.Category,item.StoreName]}
      array.push(object)
    })
    console.log(array[0].value);
})
}
getNewCases();

const storeOptions = [];
function getStoreOptions(){
  axios
  .get("http://localhost:8080/api/v1/store")
  .then((response) => {
    response.data.forEach(storeName => {
      var object = {value: storeName, label: storeName}
      storeOptions.push(object)
  });
  console.log(storeOptions);
})
  .catch((err) => {
    console.log(err);
  });
}
getStoreOptions();

function looper(){
  for(var i=0; i<array.length;i++){
    var object = {value:[array[i].value[0], array[i].value[1], array[i].value[2], <Button onClick={event =>  window.location.href='/newcases/solve'} fullWidth color="info">Solve</Button>]}
    temparray.push(object)
  }
  console.log("TEMP" + temparray);
}
//looper();

export default function newcaseModal(props){
  for(var i=0; i<array.length;i++){
    var object = {value:[array[i].value[0], array[i].value[1], array[i].value[2]]}
    temparray.push(object)
  }
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      backdrop="static"
      keyboard={false}
    >
      <ModalHeader closeButton >
        <ModalTitle id="contained-modal-title-vcenter">
          Unresolved Issues
        </ModalTitle>
      </ModalHeader>
      <ModalBody>
      <Fragment>
        <p><b>Store Location</b></p>
        <Select
          className="basic-single"
          classNamePrefix="select"
          name="color"
          options={storeOptions}
        />
      </Fragment>
      <Table
              tableHeaderColor="primary"
              tableHead={["Reported on", "Fault type", "Store Location", ""]}
              tableData={[
                array.map
              [array[0].value[0], array[0].value[1], array[0].value[2], <Button fullWidth color="info">View</Button>]
              ]}
            /> 
      </ModalBody>
      <ModalFooter>
        <Button color="danger" onClick={props.onHide}>Close</Button>
      </ModalFooter>
    </Modal>
  );
}

