import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";
import axios from 'axios';
import { Fragment } from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import { useState } from 'react';



const useStyles = makeStyles(styles);

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);
//console.log(lastURLSegment);

const displaynewCases = [];

function getNewCases() {
    axios
        .get("http://localhost:8080/api/v1/fault/" + lastURLSegment)
        .then((response) => {
            //console.log("hello");
            //console.log(displaynewCases[0].imageurl);
            //     try{
            displaynewCases.push(response.data)
            //   array.push(displaynewCases[0])
            //   testarr.push(Object.values(array[0]))
            //   console.log("xia mian");
            //   console.log(testarr);
            //     }
            //     catch(error){
            //         console.log(error)
            //     }
        })
}

getNewCases();


export default function Solve() {
    //loop for all answers and if there's 2 or more, put comma in between
    function displayAnswer(dis) {
        let test = '';
        for (let i = 0; i < dis.length; i++) {
            if (i == dis.length - 1) {
                test += dis[i];
            } else {
                test += dis[i] + ',';
            }
        }
        return test;
    }

    const [img, setimg] = useState('');
    //if data has no radio, it will display empty instead of error.
    function displayRadio(display) {
        if (display == null) {
            return 'There is no radio';
        } else {
            return display;
        }
    }
    function displayData2() {
        if (displaynewCases[0].problem.checkbox.length > 2) {
            return (displaynewCases[0].problem.checkbox.map(item => {
                return (
                    <Card>
                        <CardHeader>
                            <h4>{item.name} : </h4>
                        </CardHeader>
                        <CardBody>
                            <h5><b>{displayAnswer(item.answer)}</b></h5>
                        </CardBody>
                    </Card>
                )
            }))
        } else {
            return (
                <Card>
                    <CardHeader>
                        <h4>{displaynewCases[0].problem.checkbox.name} : </h4>
                    </CardHeader>
                    <CardBody>
                        <h5><b>{displayAnswer(displaynewCases[0].problem.checkbox.answer)}</b></h5>
                    </CardBody>
                </Card>
            )
        }
    }
    //return radio if there is 
    function tryReturnRadio() {
        try {
            return displaynewCases[0].problem.radio.name
        } catch (error) {
            return null;
        }
    }
    function tryReturnRadio2() {
        try {
            return displaynewCases[0].problem.radio.answer
        } catch (error) {
            return null;
        }
    }
    //function to retrieve image from firestore
    function retrieveImg(imgURL) {
        axios
            .get("http://localhost:8080/api/v1/image?location=" + imgURL)
            .then((response) => {
                //console.log("A@@@@");
                //console.log(response.data);
                setimg(response.data.toString());
            })
    }
    retrieveImg(displaynewCases[0].imageurl);

    const statusOptions = [{ value: "unresolved", label: "Unresolved" }, { value: "pending", label: "Pending" }, { value: "resolved", label: "Resolved" }]
    return (
        <div>
            <h3><b>Solve Case</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Reported on:</b></h4>
                        </CardHeader>
                        <CardBody>
                            <h5>{displaynewCases[0].dateTime}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Location:</b></h4>
                        </CardHeader>
                        <CardBody>
                            <h5>{displaynewCases[0].storeName}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Staff Reported:</b></h4>
                        </CardHeader>
                        <CardBody>
                            <h5>{displaynewCases[0].staffName}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Fault Type:</b></h4>
                        </CardHeader>
                        <CardBody>
                            <h5>{displaynewCases[0].problem.category}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <h3><b>Incident Details</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4>Fault Image:</h4>
                        </CardHeader>
                        <CardBody>
                            <img width="350px" height="130px" src={img} />

                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={8}>
                    <Card>
                        <CardHeader>
                            <h4>Description:</h4>
                        </CardHeader>
                        <CardBody>
                            {displaynewCases[0].description}
                        </CardBody>
                        <CardHeader>
                            <h4>Issue Status:</h4>
                        </CardHeader>
                        <CardBody>
                            <Select
                                defaultValue={{ value: "unresolved", label: "Unresolved" }}
                                className="basic-single"
                                classNamePrefix="select"
                                name="color"
                                options={statusOptions}
                                onChange={opt => console.log(opt.value)}
                            />
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <br></br>
            <br></br>
            <GridContainer>
                <GridItem xs={12} sm={12} md={6}>
                    <Card>
                        <CardHeader>
                            <h4>{displayRadio(tryReturnRadio())} :</h4>
                        </CardHeader>
                        <CardBody>
                            <h5><b>{displayRadio(tryReturnRadio2())} :</b></h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={6}>
                    {/* OLDDDDDDDDDD <Card>
                    <CardHeader>
                            <h4>{displaynewCases[0].problem.checkbox.map(item=>{
                                return item.name
                            })} :</h4>
                        </CardHeader>
                        <CardBody>
                            <h5><b>{displaynewCases[0].problem.checkbox.map(item=>{
                                return item.answer
                            })}</b></h5>
                        </CardBody>
                    </Card> */}
                    {/* {displaynewCases[0].problem.checkbox.map(item =>{
                        return(
                            <Card>
                                <CardHeader>
                                    <h4>{item.name} : </h4>
                                </CardHeader>
                                <CardBody>
                                    <h5><b>{displayAnswer(item.answer)}</b></h5>
                                </CardBody>
                            </Card>
                        )
                    })
                    } */}
                    {
                        displayData2(displaynewCases[0].problem.checkbox)
                    }
                </GridItem>
            </GridContainer>
            <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={event => window.location.href = '/admin/dashboard'} fullWidth color="success">Save</Button>
                </GridItem>
            </GridContainer>
        </div>
    );
}
