import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";


const useStyles = makeStyles(styles);

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);
//console.log(lastURLSegment);

const displaynewCases = [];
const array =[];
const testarr =[];
function getNewCases(){
    axios
    .get("http://localhost:8080/api/v1/newcases/"+lastURLSegment)
    .then((response) => {
      displaynewCases.push(response.data)
      //console.log(displaynewCases);
      array.push(displaynewCases[0])
      testarr.push( Object.values(array[0]))
      console.log(testarr);
  })
  }
  getNewCases();


export default function Solve() {
    const classes = useStyles();
    const storeOptions  = [
        {value:pending, label:pending},
        {value:resolved, label:resolved}
      ]
    const [state, setState] = React.useState({
        checkedA: true,
        checkedB: true,
      });
      const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });
      };
    
    return (
        <div>
            <h3><b>Solve Case</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Reported on:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{testarr[0].map(item=>{
                            return item.dateTime
                        })}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Location:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{testarr[0].map(item=>{
                            return item.storeName
                        })}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Staff Reported:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{testarr[0].map(item=>{
                            return item.staffName
                        })}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={3}>
                    <Card>
                        <CardHeader>
                            <h4><b>Fault Type:</b></h4>
                        </CardHeader>
                        <CardBody>
                        <h5>{testarr[0].map(item=>{
                            return item.problem.category
                        })}</h5>
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <h3><b>Incident Details</b></h3>
            <GridContainer>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4>Fault Image:</h4>
                        </CardHeader>
                        <CardBody>
                        {testarr[0].map(item=>{
                            return item.image
                        })}
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={8}>
                    <Card>
                        <CardHeader>
                            <h4>Description:</h4>
                        </CardHeader>
                        <CardBody>
                        {testarr[0].map(item=>{
                            return item.comments
                        })}
                        </CardBody>
                        <CardHeader>
                            <h4>Issue Status:</h4>
                        </CardHeader>
                        <CardBody>
                            <Fragment>
                            <p><b>Store Location</b></p>
                            <Select
                            className="basic-single"
                            classNamePrefix="select"
                            name="color"
                            options={storeOptions}
                            onChange={ e => setSearch(e.value) }
                            />
                            </Fragment>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={event =>  window.location.href='/admin/dashboard'} fullWidth color="success">Save</Button> 
                </GridItem>
            </GridContainer>
        </div>
    );
  }
