import React, { Component, Fragment } from 'react';

import Select from 'react-select';
import { colourOptions } from '../data';
import { Note } from '../styled-components';



export default function dropdown(props) {
  const Checkbox = props => <input type="checkbox" {...props} />;
  toggleClearable = () =>
  this.setState(state => ({ isClearable: !state.isClearable }));
toggleDisabled = () =>
  this.setState(state => ({ isDisabled: !state.isDisabled }));
toggleLoading = () =>
  this.setState(state => ({ isLoading: !state.isLoading }));
toggleRtl = () => this.setState(state => ({ isRtl: !state.isRtl }));
toggleSearchable = () =>
  this.setState(state => ({ isSearchable: !state.isSearchable }));

    return (
      <Fragment>
      <Select
        className="basic-single"
        classNamePrefix="select"
        defaultValue={colourOptions[0]}
        isDisabled={isDisabled}
        isLoading={isLoading}
        isClearable={isClearable}
        isRtl={isRtl}
        isSearchable={isSearchable}
        name="color"
        options={colourOptions}
      />
      <Note Tag="label">
        <Checkbox
          checked={isClearable}
          onChange={this.toggleClearable}
          id="cypress-single__clearable-checkbox"
        />
        Clearable
      </Note>
      <Note Tag="label" style={{ marginLeft: '1em' }}>
        <Checkbox
          checked={isSearchable}
          onChange={this.toggleSearchable}
          id="cypress-single__searchable-checkbox"
        />
        Searchable
      </Note>
      <Note Tag="label" style={{ marginLeft: '1em' }}>
        <Checkbox
          checked={isDisabled}
          onChange={this.toggleDisabled}
          id="cypress-single__disabled-checkbox"
        />
        Disabled
      </Note>
      <Note Tag="label" style={{ marginLeft: '1em' }}>
        <Checkbox
          checked={isLoading}
          onChange={this.toggleLoading}
          id="cypress-single__loading-checkbox"
        />
        Loading
      </Note>
      <Note Tag="label" style={{ marginLeft: '1em' }}>
        <Checkbox
          type="checkbox"
          checked={isRtl}
          onChange={this.toggleRtl}
          id="cypress-single__rtl-checkbox"
        />
        RTL
      </Note>
    </Fragment>
    );
}
