import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import axios from 'axios';

const displayFeedback=[];
const array=[];
function getCustomerFeedbacks(){
  axios
  .get("http://localhost:8080/api/v1/feedback")
  .then((response) => {
    displayFeedback.push(response.data)
    array.push(Object.values(displayFeedback[0]))
    console.log(array[0]);
  })
  }
getCustomerFeedbacks();

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const useStyles = makeStyles(styles);

export default function TableList() {
  const [search, setSearch] = useState('')

  //filter through all data instead of only 1
   const filterArray = array[0].filter(function(item){
    return Object.values(item).some( val => 
        String(val).toLowerCase().includes(search.toLowerCase()) 
    )
})
  const classes = useStyles();
  return (
    <GridContainer>
      <GridItem xs={12} sm={12} md={12}>
        <Card>
          <CardHeader color="primary">
            <h4 className={classes.cardTitleWhite}>Customer Feedbacks</h4>
            <p className={classes.cardCategoryWhite}>
              Customer Feedbacks for every outlets
            </p>
          </CardHeader>
          <CardBody>
            <Table
              tableHeaderColor="primary"
              tableHead={["Submitted On", "Store Location", "Feedback Type", "Standard", "Issue", "Ratings", "Served By",""]}
              tableData={
                filterArray.map((array) => {
                  return [array.name,array.code,array.qrstring,<Button onClick={event =>  window.location.href='/qr/editqrcode/'+array.code} fullWidth color="info">Edit</Button>]
              })
              }
            />
          </CardBody>
        </Card>
      </GridItem>
    </GridContainer>
  );
}
