import React, { Component } from 'react';
import SimpleBox from '../components/Login/SimpleBox';
import InputField from '../components/Login/InputField';
import FooterFormButtons from '../components/Login/FooterFormButtons';
import { login, getUser, googleLogin, twitterLogin } from '../actions/UserActions';
import { connect } from 'react-redux';
import ErrorAlert from '../components/Login/ErrorAlert';
import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      error: ''
    };
  }

  componentWillMount() {
    if (this.props.user !== null) {
      this.props.history.push('/');
    }
  }
  
  componentWillReceiveProps(nextProps) {
    if (nextProps.user !== null) {
      nextProps.history.push('/');
    }
  }


  submitLogin(event) {
    event.preventDefault();
    this.props.login(this.state.email, this.state.password).catch(err => {
      this.setState({
        error: err
      });
    });
  }

  renderBody() {
    const errStyle = {
      borderColor: 'red'
    };

    <MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form onSubmit={event => { this.submitLogin(event);}}>
        <p className="h5 text-center mb-4">Sign in</p>
        <div className="grey-text">
          <InputField label="Type your email" icon="envelope" group type="email" validate error="wrong"
            success="right" inputAction={(event) => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null} />
          <InputField label="Type your password" icon="lock" group type="password" validate 
          inputAction={(event) => this.setState({ password: event.target.value })}
          style={this.state.error ? errStyle : null} />
        </div>
        <div className="text-center">
          <MDBBtn>Login</MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>

    return (
      <form onSubmit={event => { this.submitLogin(event);}}>
        <div>
          <InputField id="email" type="text" label="Email"
                      inputAction={(event) => this.setState({ email: event.target.value })}
                      style={this.state.error ? errStyle : null}
          />
          <InputField id="password" type="password" label="Password"
                      inputAction={(event) => this.setState({ password: event.target.value })}
                      style={this.state.error ? errStyle : null}
          />
          {this.state.error && <ErrorAlert>Your username/password is incorrect</ErrorAlert>}
          <FooterFormButtons submitLabel="Sign in"/>
        </div>
      </form>
    );
  }

  render() {
    return (
      <div>
        <SimpleBox title="Sign in to Dashboard" body={this.renderBody()}/>
      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return { user: state.user };
}

export default connect(mapStateToProps, { login, getUser })(Login);
