import React, {useState, useEffect} from 'react';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import 'react-bootstrap-table2-filter/dist/react-bootstrap-table2-filter.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import filterFactory, { textFilter, dateFilter, Comparator } from 'react-bootstrap-table2-filter';
import * as ReactBootStrap from 'react-bootstrap';
import axios from 'axios';
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import Select from 'react-select';
import CardFooter from "components/Card/CardFooter.js";
import Button from "components/CustomButtons/Button.js";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";

export default function TableList() {
    const [customerfeedback,setCustomerFeedback] = useState([]);
    const getFeedbacksData = async ()=> {
        try{
            await axios
            .get("http://localhost:8080/api/v1/feedback")
            .then((response) => {
                console.log(response.data);
                setCustomerFeedback(Object.values(response.data));
            })
        }
        catch(e){
            console.log(e);
        }
    }

    const [search, setSearch] = useState('')
    const filterArray = analytics.filter(item=>{
    return item.storeName.toLowerCase().includes(search.toLowerCase())
  })

    const columns = [//,filter: dateFilter()
        {dataField: "dateTime", text:"Case reported on"},
        {dataField: "storeName", text:"Store Location",filter: textFilter()},
        {dataField: "problem.category", text:"Category",filter: textFilter()},
        {dataField: "description", text:"Description"},
        {dataField: "staffName", text:"Staff Name"},
        {dataField: "comments", text:"Comments"},
    ]

    useEffect(() => {
        getFeedbackData();
    },[])
    return (
        <div>
        <Card>
        <CardHeader color="primary">
        <h4>Customer Feedback</h4>
          <p>
            Feedback can be generated as excel sheet and downloaded
          </p>
        </CardHeader>
        <CardBody>
        <BootstrapTable
        filter={filterFactory()}
        keyField="uuid"
        data={customerfeedback}
        columns={columns}
        pagination={paginationFactory()}
        />
        </CardBody>
        </Card>
        <GridContainer>
            <GridItem xs={12} sm={12} md={12}>
                <Button fullWidth color="info">Generate Excel Sheet</Button>
            </GridItem>
        </GridContainer>
        </div>
    )
}

