import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "assets/jss/material-dashboard-react/layouts/adminStyle.js";
import faultimg from "assets/img/celling_aircon.jpg";
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import Switch from '@material-ui/core/Switch';
import Grid from '@material-ui/core/Grid';
import Button from "components/CustomButtons/Button.js";    
import axios from 'axios';
import { Fragment} from 'react';
import Select from 'react-select';
import pendingcaseModal from "components/Modal/pendingcaseModal";
import resolvedcaseModal from "components/Modal/resolvedcaseModal";
import {useState} from 'react';

var pageURL = window.location.href;
var lastURLSegment = pageURL.substr(pageURL.lastIndexOf('/') + 1);

const store = [];
const array = [];
const temp=[];
const temp2=[];
function getSpecificStore(){
  axios
  .get("http://localhost:8080/api/v1/store/specific/"+lastURLSegment)
  .then((response) => {
    //console.log(response.data);
      store.push(response.data)
      array.push(store[0])
      temp.push(Object.values(array[0]))
      temp2.push(Object.keys(array[0]))
      console.log("below");
      console.log(temp);
  })
}
getSpecificStore();

export default function Editstore(){
    const [storelocation,setStoreLocation] = useState("");
    const [storename,setStoreName] = useState("");
    const [storecode,setStoreCode] = useState("");
    const [storeaddress,setStoreAddress] = useState("");
    function putSpecificStore(){
        //console.log(edit)
        axios
        .put("http://localhost:8080/api/v1/store/specific/"+lastURLSegment,{
            "name": storename.toString(), 
            "code": storecode.toString(),
            "address": storeaddress.toString()
        })
        window.location.href = "/admin/functions"
      }
    return(
        <div>
            <h3><b>Edit Store</b></h3>
            <GridContainer>
            <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Location :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Edit Store Location, example..Hillion_Mall" defaultValue={temp2[0]}/>
                        </CardBody>
                    </Card>
                </GridItem>
            </GridContainer>
            <GridContainer>
            <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Name :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Edit Store Name, example..Hillion Mall" defaultValue={temp[0].map(item=>{
                            return item.name
                        })} onChange={e=>setStoreName(e.target.value)}/> 
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Code :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Edit Store Code, last 4 digit of store no" defaultValue={temp[0].map(item=>{
                            return item.code
                        })} onChange={e=>setStoreCode(e.target.value)}/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                    <Card>
                        <CardHeader>
                            <h4><b>Store Address :</b></h4>
                        </CardHeader>
                        <CardBody>
                        <input className="form-control" type="text" placeholder="Edit Store Address" defaultValue={temp[0].map(item=>{
                            return item.address
                        })} onChange={e=>setStoreAddress(e.target.value)}/>
                        </CardBody>
                    </Card>
                </GridItem>
                <GridItem xs={12} sm={12} md={12}>
                    <Button onClick={putSpecificStore} fullWidth color="success">Save</Button> 
                </GridItem>
            </GridContainer>
        </div>
    )
}
