import {
  dailySalesChart,
  emailsSubscriptionChart,
  completedTasksChart
} from "variables/charts.js";


const useStyles = makeStyles(styles);

var Chartist = require("chartist");
var delays = 80,
  durations = 500;
var delays2 = 80,
  durations2 = 500;





export default function Dashboard() {
const [unresolvedcount,setUnresolvedCount] = useState()
const [pendingcount,setPendingCount] = useState(0)
const [resolvedcount,setResolvedCount] = useState(0)

const array = [];
let test;
function getUnresolvedChart(){
  axios
  .get("http://localhost:8080/api/v1/newcaseschart")
  .then((response) => {
    console.log(response.data);
    array.push(response.data.length)
    test = array.toString();
    console.log(test);
    setUnresolvedCount(test)
  })
}

getUnresolvedChart();

const newcasesChart = {
  data: {
    labels: ["Unresolved cases today"]


    ,
    series: [[unresolvedcount]]


  },
  options: {
    axisX: {
      showGrid: false
    },
    low: 0,
    high: 8,
    chartPadding: {
      top: 0,
      right: 5,
      bottom: 0,
      left: 0
    }
  },
  responsiveOptions: [
    [
      "screen and (max-width: 640px)",
      {
        seriesBarDistance: 5,
        axisX: {
          labelInterpolationFnc: function(value) {
            return value[0];
          }
        }
      }
    ]
  ],
  animation: {
    draw: function(data) {
      if (data.type === "bar") {
